scripts.new:controller:
node -e "const fs = require('fs'); const name = process.argv[1]; fs.writeFileSync(`app/controller/${name}Controller.js`, `export default class ${name}Controller extends Controller {
    constructor() {
        super();
    }
    index() {
        super.loadView('app/view/${name.toLowerCase()}/index.html');
    }
}
`);"

scripts.new:model:
node -e "const fs = require('fs'); const name = process.argv[1]; fs.writeFileSync(`app/model/${name}Model.js`, `export default class ${name}Model extends Model {
    constructor() {
        super();
    }
}
`);"

scripts.new:js:
node -e "const fs = require('fs'); const name = process.argv[1]; fs.writeFileSync(`app/src/js/${name}.js`, `(() => {
    console.log('${name} script loaded');
})();
`);"
